INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012419407843127296, '2026-01-17 14:59:10', 'tan', '2026-01-17 14:59:10', 'tan', '连城三紀彦', '推理小说', '[\"密室杀人案\", \"多重反转\", \"心理博弈\"]', 'l:1:/20260117/宵待草夜情.png', '《宵待草夜情》是一部精巧的推理作品，通过复杂的案件设计和人物关系展开层层谜团。作者运用精湛的叙事技巧，在传统推理框架内融入现代心理分析，使故事既保持古典推理的严谨逻辑，又具备当代文学的深度思考。作品通过对犯罪动机的深入挖掘，揭示了人性的复杂与社会问题的深层矛盾。适合推理爱好者和关注人性议题的读者，能够获得智力挑战与情感共鸣的双重体验。', '[\"悬疑推理\", \"人性探索\", \"社会批判\"]', '宵待草夜情', '紧张悬疑');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012420685553954816, '2026-01-17 15:04:15', 'tan', '2026-01-17 15:04:15', 'tan', '薄伽丘', '短篇小说集', '[\"七女三男避难\", \"一百个故事\", \"佛罗伦萨瘟疫\"]', 'l:1:/20260117/十日谈.png', '《十日谈》讲述了十位青年男女为躲避黑死病，在乡村别墅避难期间每人每天讲一个故事，十天共讲述一百个故事。作品通过生动的故事展现了人文主义思想，歌颂爱情与人性，批判宗教虚伪和社会不公。薄伽丘以通俗易懂的语言和巧妙的叙事结构，创造了欧洲短篇小说的典范之作。适合喜爱古典文学和人文主义作品的读者，能深入了解文艺复兴时期的思想启蒙和社会风貌。', '[\"人性解放\", \"爱情自由\", \"宗教批判\"]', '十日谈', '轻松幽默讽刺');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012423434563710976, '2026-01-17 15:15:10', 'tan', '2026-01-17 15:15:10', 'tan', '余华', '现实主义小说', '[\"卖血求生\", \"许三观形象\", \"时代背景\"]', 'l:1:/20260117/许三观卖血记.png', '《许三观卖血记》通过主人公许三观一次次卖血的经历，展现了中国特定历史时期底层民众的生存困境与坚韧品格。作品以朴实的笔触描绘了普通人在艰难岁月中的挣扎与坚持，体现了深厚的父子情深和家庭责任感。余华用平实的语言揭示了生命的尊严与人性的光辉，具有强烈的现实关怀。适合关注社会底层、思考人生意义的读者，能深刻感受那个时代的社会风貌和人性温度。', '[\"生存苦难\", \"家庭温情\", \"人性坚韧\"]', '许三观卖血记', '悲凉中见温暖');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012431872903573504, '2026-01-17 15:48:42', 'tan', '2026-01-17 15:48:42', 'tan', '余华', '现实主义小说', '[\"福贵人生轨迹\", \"家族命运变迁\", \"时代背景映射\"]', 'l:1:/20260117/活着.png', '《活着》通过福贵一生的坎坷经历，展现了中国近现代史中普通人的生存状态。作品以朴实无华的叙述揭示了生命的脆弱与顽强，体现了人在极端困境中的求生意志。余华用简洁有力的文字构建了一个既悲惨又温暖的故事世界。这部作品不仅是个人命运的写照，更是时代变迁的缩影，具有深刻的现实意义和人文关怀。适合所有思考生命价值的读者品读。', '[\"生命意义\", \"苦难承受\", \"人性坚韧\"]', '活着', '沉重而温暖');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012432290203267072, '2026-01-17 15:50:22', 'tan', '2026-01-17 15:50:22', 'tan', '刘慈欣', '硬科幻小说', '[\"三体世界\", \"智子锁死\", \"黑暗森林\"]', 'l:1:/20260117/三体.png', '《三体》以物理学为基础构建了宏大的宇宙文明图景，通过地球与三体文明的接触展现人类文明的脆弱性。作品将科学理论与人文思考完美融合，提出了黑暗森林法则等深刻命题。其硬科幻特质和哲学思辨为读者带来震撼的阅读体验，是了解中国科幻文学的重要窗口。适合对科学、哲学感兴趣的读者深入思考人类命运。', '[\"宇宙文明\", \"科技伦理\", \"生存竞争\"]', '三体', '宏大深邃');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012432675500421120, '2026-01-17 15:51:54', 'tan', '2026-01-17 15:51:54', 'tan', '东野圭吾', '治愈系小说', '[\"神秘杂货店\", \"时空穿越\", \"信件往来\"]', 'l:1:/20260117/解忧杂货店.png', '《解忧杂货店》通过一家神奇的杂货店，展现了三个小偷与过去顾客之间的奇妙联系。书中通过信件交流的方式，探讨了人生的各种困惑和选择，体现了人与人之间的温情与善意。东野圭吾巧妙地将悬疑元素融入治愈故事中，创造了一个充满希望和感动的世界。适合寻求心灵慰藉的读者，能让人重新思考生活的意义和人际关系的珍贵。', '[\"温暖人心\", \"人生选择\", \"因果关系\"]', '解忧杂货店', '温馨感人');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012432971878330368, '2026-01-17 15:53:04', 'tan', '2026-01-17 15:53:04', 'tan', '尤瓦尔·赫拉利', '历史科普著作', '[\"认知革命\", \"农业革命\", \"科学革命\"]', 'l:1:/20260117/人类简史.png', '本书从认知革命、农业革命到科学革命，全景式展现人类发展历程，颠覆传统历史观。作者以宏大的视角重新审视人类文明，揭示历史发展的深层逻辑和规律。适合对人类历史感兴趣的读者，能获得全新的思维启发。', '[\"人类演化\", \"文明发展\", \"社会变革\"]', '人类简史', '理性客观深刻');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012434659427184640, '2026-01-17 15:59:47', 'tan', '2026-01-17 15:59:47', 'tan', '连城三紀彦', '推理小说', '[\"桔梗花象征\", \"连环案件\", \"细腻描写\"]', 'l:1:/20260117/一朵桔梗花_.png', '《一朵桔梗花》是日本推理作家连城三纪彦的短篇小说集，以精巧的构思和诗意的语言展现了一系列围绕桔梗花展开的悬疑故事。作品将推理与文学性完美结合，在解谜过程中深入探讨人性的复杂与社会的阴暗面。作者用细腻的心理描写和唯美的文笔，创造出独特的推理美学风格。适合喜爱文学性推理作品的读者，能在享受推理乐趣的同时感受深层的人文思考。', '[\"悬疑推理\", \"人性探索\", \"社会现实\"]', '一朵桔梗花 ', '冷静客观');
INSERT INTO `tb_book_analyse` (`id`, `create_time`, `create_user_name`, `update_time`, `update_user_name`, `author`, `genre`, `key_elements`, `poster_url`, `recommendation`, `themes`, `title`, `tone`) VALUES (2012436886472585216, '2026-01-17 16:08:38', 'tan', '2026-01-17 16:08:38', 'tan', '格拉宁', '人物传记', '[\"柳比歇夫\", \"时间统计法\", \"昆虫学家\"]', 'l:1:/20260117/奇特的一生.png', '本书讲述了苏联昆虫学家柳比歇夫通过独创的时间统计法，精确记录并利用每一分钟，从而在多领域取得惊人成就的一生。其独特价值在于展示了一种通过极致理性规划实现精神自由和人生充实的生活哲学。适合渴望提升效率、探索生命意义及寻求自我突破的读者阅读，让人重新思考时间的价值。', '[\"时间管理\", \"人生意义\", \"极致自律\"]', '奇特的一生', '理性客观');

-- ============================================
-- poster_url 格式说明
-- ============================================
-- 统一格式: 协议:鉴权:路径
--
-- 协议代码:
--   h    - HTTP/HTTPS
--   ali  - 阿里云 OSS
--   qiniu - 七牛云
--   s3   - AWS S3
--   f    - FTP
--   l    - 本地存储
--
-- 鉴权标识:
--   0 - 无需鉴权（公开访问）
--   1 - 需要鉴权（需配置凭证）
--
-- 示例:
--   h:0:https://images.unsplash.com/photo-xxx.jpg           # 无鉴权HTTP
--   h:1:https://private-cdn.com/books/xxx.png               # 有鉴权HTTP
--   ali:1:/my-bucket/books/三体.png                          # 阿里云OSS（需鉴权）
--   qiniu:0:https://cdn.qiniu.com/books/xxx.png             # 七牛云公开URL
--   s3:1:/my-bucket/books/xxx.png                           # AWS S3（需鉴权）
--   f:0:ftp://192.168.1.100/public/books/xxx.png            # FTP无鉴权
--   f:1:ftp://192.168.1.100/private/books/xxx.png           # FTP需鉴权
--   l:0:/public/20240115/三体.png                            # 本地公开目录
--   l:1:/20240115/三体.png                                   # 本地私有目录（AI生成）
